import React, { useState } from 'react';
import { fetchFirstAid, parseInjuryWithAI } from '../services/api';

const InjuryForm = () => {
  const [injury, setInjury] = useState('');
  const [steps, setSteps] = useState<string[]>([]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const result = await fetchFirstAid(injury);
    setSteps(result.steps);
  };

  const handleAIParse = async () => {
    const data = await parseInjuryWithAI(injury);
    if (data.injury && data.injury !== "unknown") {
      const result = await fetchFirstAid(data.injury);
      setSteps(result.steps);
    } else {
      setSteps(["Sorry, we couldn't recognize the injury from your input."]);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input
        type="text"
        value={injury}
        onChange={(e) => setInjury(e.target.value)}
        placeholder="Describe the injury (e.g., 'My friend has a deep cut')"
        className="p-2 border border-gray-300 rounded w-full"
      />
      <div className="flex space-x-2">
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Search Manually</button>
        <button type="button" onClick={handleAIParse} className="bg-green-500 text-white px-4 py-2 rounded">Use AI to Parse</button>
      </div>
      <ul className="list-disc list-inside mt-4">
        {steps.map((step, index) => <li key={index}>{step}</li>)}
      </ul>
    </form>
  );
};

export default InjuryForm;
